mapper.update:
update pm_attachment
business_type = #{businessType}, business_id = #{businessId}, document_type = #{documentType}, file_name = #{fileName}, file_path = #{filePath}, file_size = #{fileSize}, file_type = #{fileType}, file_description = #{fileDescription}, del_flag = #{delFlag}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where attachment_id = #{attachmentId}

mapper.select:
select count(*) from pm_attachment
where del_flag = '0'
    and business_type = #{businessType}
    and business_id = #{businessId}
