mapper.select:
select c.contract_id, c.contract_code, c.contract_name, c.customer_id, c.dept_id, c.contract_type, c.contract_status, c.contract_sign_date, c.contract_period, c.contract_amount, c.tax_rate, c.amount_no_tax, c.tax_amount, c.confirm_amount, c.confirm_year, c.free_maintenance_period, c.create_by, c.create_time, c.update_by, c.update_time, c.remark, d.dept_name as dept_name, cust.customer_simple_name as customer_name, dict1.dict_label as contract_type_name, dict2.dict_label as contract_status_name, u1.nick_name as create_by_name, u2.nick_name as update_by_name from pm_contract c left join sys_dept d on c.dept_id = d.dept_id left join pm_customer cust on c.customer_id = cust.customer_id left join sys_dict_data dict1 on c.contract_type = dict1.dict_value and dict1.dict_type = 'sys_htlx' left join sys_dict_data dict2 on c.contract_status = dict2.dict_value and dict2.dict_type = 'sys_htzt' left join sys_user u1 on c.create_by = u1.user_name left join sys_user u2 on c.update_by = u2.user_name
c.del_flag = '0' and c.contract_code = #{contractCode} and c.contract_name = #{contractName} and c.customer_id = #{customerId} and c.dept_id = #{deptId} and c.contract_type = #{contractType} and c.contract_status = #{contractStatus} and c.confirm_year = #{confirmYear}
order by c.create_time desc

select
    IFNULL(SUM(c.contract_amount), 0) as contractAmountSum,
    IFNULL(SUM(c.amount_no_tax), 0) as amountNoTaxSum
from pm_contract c

    c.del_flag = '0'
     and c.contract_code = #{contractCode}
     and c.contract_name = #{contractName}
     and c.customer_id = #{customerId}
     and c.dept_id = #{deptId}
     and c.contract_type = #{contractType}
     and c.contract_status = #{contractStatus}
     and c.confirm_year = #{confirmYear}

mapper.update:
update pm_contract
contract_code = #{contractCode}, contract_name = #{contractName}, customer_id = #{customerId}, dept_id = #{deptId}, contract_type = #{contractType}, contract_status = #{contractStatus}, contract_sign_date = #{contractSignDate}, contract_period = #{contractPeriod}, contract_amount = #{contractAmount}, tax_rate = #{taxRate}, amount_no_tax = #{amountNoTax}, tax_amount = #{taxAmount}, confirm_amount = #{confirmAmount}, confirm_year = #{confirmYear}, free_maintenance_period = #{freeMaintenancePeriod}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, reserved_field1 = #{reservedField1}, reserved_field2 = #{reservedField2}, reserved_field3 = #{reservedField3}, reserved_field4 = #{reservedField4}, reserved_field5 = #{reservedField5},
where contract_id = #{contractId}
